SELECT u.phone AS phone, u.nick_name AS nick_name, u.head_pic AS head_pic, c.content AS content, c.order_code AS order_code, c.order_time AS order_time, c.r_code AS r_code, c.rl_code AS rl_code, rl.`name` AS rl_name FROM t_lp_comment AS c LEFT JOIN t_user AS u ON c.create_user = u.user_code LEFT JOIN t_report_level AS rl ON c.rl_code = rl.`code`
c.`lp_code` = #{lpCode,jdbcType=VARCHAR}
and c.`level` = #{level,jdbcType=INTEGER}
ORDER BY c.create_time DESC LIMIT 5

