mapper.select:
SELECT o.`code` AS `code`, o.pay_price AS pay_price, o.pay_code as pay_code, o.`status` AS `status`, r.title, r.pic AS pic, r.image AS image, r.path, rl.`name` AS level_name, lp.addressFull AS address, o.update_time AS update_time FROM t_order AS o LEFT JOIN t_report AS r ON o.report_code = r.`code` LEFT JOIN t_landed_property AS lp ON r.houses_code = lp.`code` LEFT JOIN t_report_level AS rl ON r.level_code = rl.`code`
o.create_user = #{createUser,jdbcType=VARCHAR}
AND o.`status` = #{status,jdbcType=INTEGER}
order by o.update_time desc limit #{start,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}

SELECT count(1) FROM t_order AS o LEFT JOIN t_report AS r ON o.report_code = r.`code` LEFT JOIN t_landed_property AS lp ON r.houses_code = lp.`code` LEFT JOIN t_report_level AS rl ON r.level_code = rl.`code`
o.create_user = #{createUser,jdbcType=VARCHAR}
AND o.`status` = #{status,jdbcType=INTEGER}
