mapper.select:
SELECT m.`code`, m.content, DATE_FORMAT( m.create_time, '%Y-%m-%d' ) AS create_time, m.is_read, c.`code` as type_code, c.`name` as type_name, outer_code FROM t_message AS m LEFT JOIN sys_define_code AS c ON m.type_code = c.`code`
m.received_user = #{receivedUser,jdbcType=VARCHAR}
and m.is_read = #{isRead,jdbcType=INTEGER}
limit #{start,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}

select count(1) from t_message AS m LEFT JOIN sys_define_code AS c ON m.type_code = c.`code`
m.received_user = #{receivedUser,jdbcType=VARCHAR}
and m.is_read = #{isRead,jdbcType=INTEGER}
